//*****************************************************************************
//
// This function performs basic board level initialization.
//
//*****************************************************************************
void
initDriver(void)
{
    //
    // Configure the pinout for the board.
    //
    PinoutSet();

    //
    // Set the system clock and initialize GPIOs used in this configuration.
    //
    pio_init();

    //
    // Enable processor interrupts.
    //
    MAP_IntMasterEnable();

    //
    // Now configure UART0 for output.
    //
    UARTStdioConfig(0, 115200, g_ui32SysClock);

    //
    // Tell the user that we're up and running.
    //
    UARTprintf("\nCC3000 Patch Programmer Started.\n");
}

//*****************************************************************************
//
// This function starts up the CC3000.
//
//*****************************************************************************
void
initCC3000(unsigned short cRequestPatch)
{
    //
    // Perform basic setup once again.  In addition to configuring pins, this
    // also performs some of the initial signalling to the CC3000.
    //
    pio_init();

    //
    // Initialize the SPI and IRQ lines connecting the CC3000.  We run the SPI
    // interface at 1MHz.
    //
    init_spi(1000000, g_ui32SysClock);

    //
    // Tell the WiFi driver which application- and board-specific functions
    // to call in response to various events and when interrupt and pin
    // control is required.
    //
    UARTprintf("Initializing CC3000 WiFi stack... ");
    wlan_init( CC3000_UsynchCallback, sendWLFWPatch, sendDriverPatch,
                        sendBootLoaderPatch, ReadWlanInterruptPin,
                        WlanInterruptEnable, WlanInterruptDisable,
                        WriteWlanPin);

    //
    // Start the WiFi stack.
    //
    UARTprintf("Done.\nStarting WiFi stack... ");
    wlan_start(cRequestPatch);

    //
    // Turn on the LED 1 (RED) to indicate that we are active and initiated
    // WiFi successfully.
    //
    turnLedOn(RED_LED);
    UARTprintf("Done.\n");

    wlan_smart_config_set_prefix(pcCC3000_prefix);

    //
    // Mask out all non-required events from CC3000.
    //
    wlan_set_event_mask(HCI_EVNT_WLAN_KEEPALIVE | HCI_EVNT_WLAN_UNSOL_INIT |
                        HCI_EVNT_WLAN_ASYNC_PING_REPORT);

    //
    // Set up the periodic system tick and enable its interrupt.
    //
    InitSysTick();
}

//*****************************************************************************
//
// This function writes the file allocation table into EEPROM on the CC3000
// BoosterPack or Evaluation Module.
//
//*****************************************************************************
static unsigned char
fat_write_content(unsigned short const *file_address,
                  unsigned short const *file_length)
{
    uint32_t ui32Index;
    unsigned char ucStatus;
    unsigned char fatTable[(NVMEM_RM_FILEID + 1) * 4];
    unsigned char *fatTablePtr = fatTable;

    //
    // Write the magic number at the start of the FAT.
    //
    ucStatus = nvmem_write(16, 2, 0, (unsigned char *)"LS");

    //
    // Loop through each of the file IDs in the FAT, updating the table
    // content.
    //
    for (ui32Index = 0; ui32Index <= NVMEM_RM_FILEID; ui32Index++)
    {
        //
        // Write the lower address byte and mark the entry as allocated.
        //
        *fatTablePtr++ = (unsigned char)(file_address[ui32Index] & 0xff) | BIT0;

        //
        // Write the upper address byte.
        //
        *fatTablePtr++ = (unsigned char)((file_address[ui32Index]>>8) & 0xff);

        //
        // Write the lower length byte.
        //
        *fatTablePtr++ = (unsigned char)(file_length[ui32Index] & 0xff);

        //
        // Write the upper length byte.
        //
        *fatTablePtr++ = (unsigned char)((file_length[ui32Index]>>8) & 0xff);
    }

    //
    // Write the newly-completed FAT to EEPROM.
    //
    ucStatus = nvmem_write(16, (NVMEM_RM_FILEID + 1) * 4, 4, fatTable);

    //
    // Erase any user files that exist in EEPROM.
    //
    memset(fatTable, 0, sizeof(fatTable));
    ucStatus = nvmem_write(16, 16, 52, fatTable);

    //
    // Tell the caller whether or not the function was successful.
    //
    return(ucStatus);
}

//*****************************************************************************
//
// Print a line of formatted text at a given character position on the LCD
// display and also output it to UART0.
//
//*****************************************************************************
void
PrintAt(uint16_t ui16X, uint16_t ui16Y, char *pcFormat, ...)
{
    va_list arg;
    char pcBuffer[MAX_STRING_LEN];
    int iNumChars;
    tRectangle sRect;
    uint32_t ui32CharWidth, ui32CharHeight;

    //
    // Start the varargs processing.
    //
    va_start(arg, pcFormat);

    //
    // Call vsnprintf to format the string into our buffer.
    //
    iNumChars = uvsnprintf(pcBuffer, MAX_STRING_LEN, pcFormat, arg);

    //
    // Output the result to the UART.
    //
    UARTprintf("%s\n", pcBuffer);

    //
    // Get the character cell size of the default font.
    //
    ui32CharWidth = GrFontMaxWidthGet(TEXT_FONT);
    ui32CharHeight = GrFontHeightGet(TEXT_FONT);

    //
    // Calculate the bounding box for this line of text.
    //
    sRect.i16XMin = (ui32CharWidth * ui16X) + TEXT_AREA_LEFT;
    sRect.i16XMax = GrContextDpyWidthGet(&g_sContext) - TEXT_AREA_RIGHT;
    sRect.i16YMin = ((ui32CharHeight + TEXT_LINE_PADDING) * ui16Y) +
                    TEXT_AREA_TOP;
    sRect.i16YMax = sRect.i16YMin + ui32CharHeight + TEXT_LINE_PADDING;

    //
    // Fill the bounding box with black.
    //
    GrContextForegroundSet(&g_sContext, ClrBlack);
    GrRectFill(&g_sContext, &sRect);

    //
    // Set the clipping rectangle to the bounding box.
    //
    GrContextClipRegionSet(&g_sContext, &sRect);

    //
    // Set the foreground color to white.
    //
    GrContextForegroundSet(&g_sContext, ClrWhite);

    //
    // Draw the text.
    //
    GrStringDraw(&g_sContext, pcBuffer, iNumChars, sRect.i16XMin,
                 sRect.i16YMin, false);
}

//*****************************************************************************
//
// Application main entry function.
//
//*****************************************************************************
int
main(void)
{
    unsigned char  *pRMParams;

    //
    // Initialize the hardware and indicate that patches are available.
    //
    initDriver();
    initCC3000(PATCHES_FROM_HOST);

    //
    // Initialize the display driver.
    //
    Kentec320x240x16_SSD2119Init(g_ui32SysClock);

    //
    // Initialize the graphics context.
    //
    GrContextInit(&g_sContext, &g_sKentec320x240x16_SSD2119);

    //
    // Draw the application frame.
    //
    FrameDraw(&g_sContext, "cc3000-patch-programmer");

    SysCtlDelay(16500000);

    //
    // Read the CC3000 MAC address from its EEPROM.
    //
    PrintAt(0, 0, "Reading existing MAC address.");

    mac_status = nvmem_get_mac_address(cMacFromEeprom);

    //
    // Assume no error until we determine otherwise.
    //
    ucReturnStatus = 1;

    PrintAt(0, 1, "Reading existing RM parameters.");

    while ((ucReturnStatus) && (counter < 3))
    {
        // read RM parameters
        ucReturnStatus = nvmem_read(NVMEM_RM_FILEID, 128, 0,
                                    cRMParamsFromEeprom);

        counter++;
    }

    //
    // If we failed to read the RM file successfully after 3 attempts, load the
    // default values instead.
    //
    if (counter == 3)
    {
        PrintAt(0, 2, "None found - using defaults.");
        pRMParams = (unsigned char *)cRMdefaultParams;
    }
    else
    {
        PrintAt(0, 2, "Parameters found.");
        pRMParams = cRMParamsFromEeprom;
    }

    //
    // Again, assume all is well before we write the new FAT to EEPROM.
    //
    ucReturnStatus = 1;

    PrintAt(0, 3, "Writing new FAT to EEPROM.");

    while (ucReturnStatus)
    {
        //
        // Write the new FAT to EEPROM.
        //
        ucReturnStatus = fat_write_content(aFATEntries[0], aFATEntries[1]);
    }

    SysCtlDelay(16500000);

    PrintAt(0, 4, "Writing RM parameters to EEPROM.");

    ucReturnStatus = 1;

    while (ucReturnStatus)
    {
        //
        // Write the RM parameters to EEPROM.
        //
        ucReturnStatus = nvmem_write(NVMEM_RM_FILEID, 128, 0, pRMParams);
    }

    ucReturnStatus = 1;

    //
    // Write the MAC address back to EEPROM if it previously existed.
    //
    if (mac_status == 0)
    {
        // zero out MCAST bit if set
        cMacFromEeprom[0] &= 0xfe;

        PrintAt(0, 5, "Writing MAC address to EEPROM.");

        while (ucReturnStatus)
        {
            ucReturnStatus = nvmem_set_mac_address(cMacFromEeprom);
        }
    }
    else
    {
        PrintAt(0, 5, "No EEPROM address to write.");
    }

    ucStatus_Dr = 1;

    PrintAt(0, 6, "Writing driver patch to EEPROM.");

    while (ucStatus_Dr)
    {
        //
        // Write the driver patch to EEPRROM.
        //
        ucStatus_Dr = nvmem_write_patch(NVMEM_WLAN_DRIVER_SP_FILEID,
                                        drv_length, wlan_drv_patch);
    }

    ucStatus_FW = 1;

    PrintAt(0, 7, "Writing firmware patch to EEPROM.");

    while (ucStatus_FW)
    {
        //
        // Write the firmware patch to EEPRROM.
        //
        ucStatus_FW = nvmem_write_patch(NVMEM_WLAN_FW_SP_FILEID, fw_length,
                                        fw_patch);
    }

    //
    // The next call to initCC3000 will muck with the system clock which,
    // in turn, mucks with the UART baud rate.  We need to pause here to
    // let all UART activity complete before continuing.  If we don't do
    // this, we end up corrupting the UART output.
    //
    while(UARTBusy(UART0_BASE))
    {
        //
        // Just wait a while.
        //
    }

    //
    // Re-initialize the WiFi and this time request that it load patches from
    // its EEPROM.
    //
    initCC3000(PATCHES_FROM_EEPROM);

    //
    // Tell the world that the patch programmer finished.
    //
    PrintAt(0, 9, "Programming completed.");

    //
    // We're finished so just spin here.
    //
    while(1)
    {
        //
        // Put the CPU to sleep since we have nothing else to do.
        //
        CPUwfi();
    }
}
